export type IconType =
	| "home"
	| "home-alt"
	| "watch"
	| "tvGuide"
	| "tvGuide-alt"
	| "search"
	| "onDemand"
	| "onDemand-alt"
	| "settings"
	| "settings-alt"
	| "user"
	| "warning";

type Props = {
	icon: IconType;
	id?: string;
	width?: number;
	height?: number;
	className?: string;
	style?: React.CSSProperties;
};

export function Icon(props: Props) {
	switch (props.icon) {
		case "home":
			return <Home {...props} />;
		case "home-alt":
			return <HomeAlt {...props} />;
		case "watch":
			return <Watch {...props} />;
		case "tvGuide":
			return <TvGuide {...props} />;
		case "tvGuide-alt":
			return <TvGuideAlt {...props} />;
		case "search":
			return <Search {...props} />;
		case "onDemand":
			return <OnDemand {...props} />;
		case "onDemand-alt":
			return <OnDemandAlt {...props} />;
		case "settings":
			return <Settings {...props} />;
		case "settings-alt":
			return <SettingsAlt {...props} />;
		case "user":
			return <User {...props} />;
		case "warning":
			return <Warning {...props} />;
	}
}

function getColors(style?: React.CSSProperties): {
	fill?: string;
	stroke?: string;
} {
	const fill = style?.fill ?? style?.color;
	const stroke = style?.stroke ?? style?.color;

	return { fill, stroke };
}

